import React, { useState } from 'react';
import styled from 'styled-components';
import Router from "next/router";
import { IoSearchSharp } from "react-icons/io5";

import AppLayout from '../../components/layout/AppLayout';
import MovieInfoLink from '../../components/MovieInfoLink'

import { getMovieFromTMDB } from '../api/api';

const SearchInput = styled.input`
    width: 250px;
    height: 50px;
    margin: 0 auto;
    text-align: center;
    border-radius: 2rem;
    border: none;
`
function MovieSearchPage(props) {
    const { movieDetail } = props;

    const [inputValue, setInputValue] = useState("");

    const handleInputValue = (e) => {
        setInputValue(e.target.value)
    };

    const handleSubmitValue = (e) => {
        e.preventDefault();
        Router.push(encodeURI(`/movieSearch/${inputValue}`));
    };

    return (
        <AppLayout>
            <form
                onSubmit={handleSubmitValue}
                style={{ textAlign: 'center', position: 'relative', top: '2.5rem', left: '2rem' }}
            >
                <SearchInput
                    placeholder="영화명을 입력해주세요."
                    onChange={handleInputValue}
                />
                <button type="submit" className="btn search-btn">
                    <IoSearchSharp style={{ color: 'white', fontSize: '1.5rem' }} />
                </button>
            </form>
            <div>
                {
                    !movieDetail.results && (
                        <div>검색하신 영화가 존재하지 않습니다.</div>
                    )
                }
                {
                    movieDetail.results && (
                        <>
                            <div style={{ display: 'flex', flexWrap: 'wrap', width: '80%', margin: '0 auto', marginTop: '2rem' }}>
                            {
                                movieDetail.results.map(movieItem => (
                                    <div key={movieItem.id} style={{ width: '210px', margin: '0 1rem' }}>
                                        <MovieInfoLink
                                            id={movieItem.id}
                                            src={movieItem.poster_path}
                                            koreanTitle={movieItem.title}
                                        />
                                    </div>
                                ))
                            }
                            </div>
                        </>
                    )
                }
            </div>
        </AppLayout>
    )
};

export async function getServerSideProps({ query }) {
    const movieDetail = await getMovieFromTMDB(encodeURI(query.name));
    
    return {
        props: {
            movieDetail: movieDetail,
        },
    }
}

export default MovieSearchPage;
