import React, { useState, useEffect } from 'react'
import { getMovieList } from '../api'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [moviePoster, setMoviePoster] = useState([])

    useEffect(() => {
        const fetchMovieList = async () => {
            const result = await getMovieList();
            const movieList = result.boxOfficeResult.dailyBoxOfficeList;
            console.log(movieList);
            movieList.forEach(movieItem => {
                console.log(movieItem.movieNm);
                const movieName = movieItem.movieNm;
                const getNaverMovie = async () => {
                    try {
                        const res = await axios.get('http://localhost:5000/naver/getNaverMovie', {
                            params: {
                                query: movieName,
                            },
                        });
                        console.log(res);
                        if(res && res.status === 200) {
                            console.log(res.data);
                        }
                    } catch (err) {
                        console.log('error', err);
                    }
                }
                getNaverMovie();
            });
        }
        fetchMovieList();
    }, [])
    /*
    const movieNm = '미나리'

    useEffect(() => {

    }, [])
    */

    return (
        <div>
            {/* {
                movieList.map(item => (
                    <div key={item.rnum}>{item.movieNm}</div>
                ))
            } */}
            <img src={moviePoster} />
            {/* <button onClick={getNaverMovie}>클릭</button> */}
        </div>
    )
}

export default Home;
