import React from 'react'
import Slider from "react-slick";
import styled from 'styled-components'

const ActorImage = styled.img`
  width: '200px';
  height: '300px';
  border-radius: '5px';
  transition: 500ms ease;

  &:hover {
    opacity: 0.5;
  }
`


function MovieStllSlider({ movieActorInfo }) {
    const settings = {
        dots: false, 
        infinite: true,
        slidesToShow: 5,
        slidesToScroll: 5,
        speed: 500,
        arrows: false,
        autoplay: false,
        className:"wrap-slider",
        initialSlide: 0,
        responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 650,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    const imageURL = 'https://image.tmdb.org/t/p/w200/'

    const havePosterPath = (posterPath) => {
      if(!posterPath) {
          posterPath = "https://res.cloudinary.com/dvmqbovxh/image/upload/v1616866172/noposter_pcwcby.png";
          return posterPath;
      } else {
          return `${imageURL}${posterPath}`
      }
  };
    return (
        <Slider {...settings}>
            {
                movieActorInfo.map(item => (
                    <div>
                        <ActorImage src={havePosterPath(item.profile_path)} />
                        <div style={{ position: 'absolute', top: 0, left: 0 }}>{item.name}</div>
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieStllSlider
