import React, { useState, useEffect } from 'react'
import styled from 'styled-components'

import AppLayout from '../components/AppLayout'
import MovieSlider from '../components/MovieSlider'

import MovieInfo from '../json/movieInfo.json'

import { getMovieList, getKMDB } from '../api'

const SearchForm = styled.form`
    text-align: center;
`

const SearchInput = styled.input`
    text-align: center;
    border: 1px solid gray;
    padding: .5rem .7rem;
    border-radius: 5px;
`

function Home() {
    const { movieInfomation } = MovieInfo.movie
    const [boxofficeMovieNames, setboxofficeMovieNames] = useState([])
    const [inputValue, setInputValue] = useState([])

    const handleInputValue = (e) => {
        setInputValue(e.target.value)
    }

    const handleSubmitValue = () => {
        // inputValue
    }

    useEffect(() => {
        const fetchMovieList = async () => {
            const boxofficeList = await getMovieList();
            setboxofficeMovieNames(movieNameArray(boxofficeList.boxOfficeResult.dailyBoxOfficeList));
            const result2 = await getKMDB("중경삼림");
            console.log(result2);
        }
        fetchMovieList();
    }, [])

    const movieNameArray = (list) => {
        var arr = [];
        
        for(let i=0; i<list.length; i++) {
            arr.push(list[i].movieNm)
        }
        return arr;
    }

    return (
        <AppLayout>
            <h2 style={{ textAlign: 'center' }}>박스오피스</h2>
            <MovieSlider movieInfomation={movieInfomation} />
            <SearchForm onSubmit={handleSubmitValue} className="form search-form">
                <SearchInput placeholder="영화명을 입력해주세요." onChange={handleInputValue} />
                <button>검색</button>
            </SearchForm>
        </AppLayout>
    )
}

export default Home;
