import React from 'react';
import styled from 'styled-components';

const CategoryBtn = styled.button`
    width: 80px;
    border: none;
    padding: 8px 0;
    border-radius: 15px;
    color: white;
    background:
        ${props => props.isOpened === 'Action' ? 'rgba(204, 32, 32, 0.637)' :
                   props.isOpened === 'Fantasy' ? 'rgba(204, 32, 32, 0.3)' :
                   props.isOpened === 'Animation' ? 'rgba(204, 32, 32, 0.3)' : 'none'
        };

    box-sizing: border-box;

    &:hover {
        background: rgba(255, 0, 0, 0.8);
    }
`

function CategoryBotton({ genre }) {
    return (
        <CategoryBtn genre={genre}>{genre}</CategoryBtn>
    )
};

export default CategoryBotton;
