import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Router, { useRouter } from "next/router";
import { IoSearchSharp } from "react-icons/io5";
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../../store/configureStore';
import SubPageLayout from '../../components/layout/SubPageLayout';
import MovieInfoLink from '../../components/MovieInfoLink'

import { LOAD_MOVIE_FROM_TMDB_REQUEST } from '../../reducers/tmdb';

const SearchInput = styled.input`
    width: 250px;
    height: 50px;
    margin: 0 auto;
    text-align: center;
    border-radius: 32px;
    border: none;
`

const MovieCardWrap = styled.div`
    margin-left: 5%;
    position: relative;
    top: 128px;
    display: flex;
    flex-wrap: wrap;
`

function MovieSearchPage() {
    const dispatch = useDispatch()
    const router = useRouter();

    const { name } = router.query;
    const { movieSearchResult } = useSelector((state) => state.movie);

    const [inputValue, setInputValue] = useState("");

    const handleInputValue = (e) => {
        setInputValue(e.target.value);
    };

    const handleSubmitValue = (e) => {
        e.preventDefault();
        Router.push(`/movieSearch/${inputValue}`);
    };

    const handleLoadMoreMovie = () => {
        dispatch({
            type: LOAD_MOVIE_FROM_TMDB_REQUEST,
            data: {
                movieName: name,
                pageNum: 2,
            }
        })
    };

    return (
        <SubPageLayout>
            <div style={{ position: 'relative' }}>
                <form
                    onSubmit={handleSubmitValue}
                    style={{ textAlign: 'center', position: 'absolute', top: '40px', left: '32px' }}
                >
                    <SearchInput
                        placeholder="영화명을 입력해주세요."
                        onChange={handleInputValue}
                    />
                    <button type="submit" className="btn search-btn">
                        <IoSearchSharp style={{ color: 'white', fontSize: '24px' }} />
                    </button>
                </form>
                {
                    !movieSearchResult.results[0] && (
                        <div>검색하신 영화가 존재하지 않습니다.</div>
                    )
                }
                {
                    movieSearchResult.results && (
                        <MovieCardWrap>
                        {
                            movieSearchResult.results.map(movieItem => (
                                <div key={movieItem.id} style={{ width: '210px', margin: '0 16px' }}>
                                    <MovieInfoLink
                                        id={movieItem.id}
                                        src={movieItem.poster_path}
                                        koreanTitle={movieItem.title}
                                    />
                                </div>
                            ))
                        }
                        </MovieCardWrap>
                    )
                }
                <button onClick={handleLoadMoreMovie}>Load More</button>
            </div>
        </SubPageLayout>
    )
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    context.store.dispatch({
        type: LOAD_MOVIE_FROM_TMDB_REQUEST,
        data: {
            movieName: context.query.name,
            pageNum: 1,
        }
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();
});


// export async function getServerSideProps({ query }) {
//     const movieDetail = await getMovieFromTMDB(encodeURI(query.name), 1);
    
//     return {
//         props: {
//             movieDetail: movieDetail,
//         },
//     }
// };

export default MovieSearchPage;
