import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Router, { useRouter } from "next/router";
import { IoSearchSharp } from "react-icons/io5";

import SubPageLayout from '../../components/layout/SubPageLayout';
import MovieInfoLink from '../../components/MovieInfoLink'

import { getMovieFromTMDB } from '../api/api';

const SearchInput = styled.input`
    width: 250px;
    height: 50px;
    margin: 0 auto;
    text-align: center;
    border-radius: 32px;
    border: none;
`

const MovieCardWrap = styled.div`
    margin-left: 5%;
    position: relative;
    top: 128px;
    display: flex;
    flex-wrap: wrap;
`

function MovieSearchPage({ movieDetail }) {
    const router = useRouter();
    const { name } = router.query;
    
    const [inputValue, setInputValue] = useState("");
    const [movieList, setMovieList] = useState([]);
    const [CurrentPage, setCurrentPage] = useState(1);
    
    useEffect(() => {
        setMovieList(movieDetail.results); // 이렇게만 저장하면 .... 페이지가 바뀌는 것 처럼 보이는 것이 불가...
    }, [movieDetail.results]);              // 이렇게하면... load가 불가....

    const handleInputValue = (e) => {
        setInputValue(e.target.value);
    };

    const handleSubmitValue = (e) => {
        e.preventDefault();
        Router.push(`/movieSearch/${inputValue}`);
    };

    const handleLoadMoreMovie = async () => {
        const movieFromTMDB = await getMovieFromTMDB(name, `${CurrentPage + 1}`);
        const result = movieList.concat(movieFromTMDB.results)
        setMovieList(result);
        setCurrentPage(CurrentPage + 1);
    };

    return (
        <SubPageLayout>
            <div style={{ position: 'relative' }}>
                <form
                    onSubmit={handleSubmitValue}
                    style={{ textAlign: 'center', position: 'absolute', top: '40px', left: '32px' }}
                >
                    <SearchInput
                        placeholder="영화명을 입력해주세요."
                        onChange={handleInputValue}
                    />
                    <button type="submit" className="btn search-btn">
                        <IoSearchSharp style={{ color: 'white', fontSize: '24px' }} />
                    </button>
                </form>
                {/* {
                    !movieList.results[0] && (
                        <div>검색하신 영화가 존재하지 않습니다.</div>
                    )
                } */}
                {
                    movieList && (
                        <MovieCardWrap>
                        {
                            movieList.map(movieItem => (
                                <div key={movieItem.id} style={{ width: '210px', margin: '0 16px' }}>
                                    <MovieInfoLink
                                        id={movieItem.id}
                                        src={movieItem.poster_path}
                                        koreanTitle={movieItem.title}
                                    />
                                </div>
                            ))
                        }
                        </MovieCardWrap>
                    )
                }
                <button onClick={handleLoadMoreMovie}>Load More</button>
            </div>
        </SubPageLayout>
    )
};

export async function getServerSideProps({ query }) {
    const movieDetail = await getMovieFromTMDB(encodeURI(query.name), 1);
    
    return {
        props: movieDetail
    }
};

export default MovieSearchPage;
