import React, { useState, useEffect } from 'react'
import axios from 'axios'
import AppLayout from '../components/AppLayout'

import { getMovieList, getKMDB } from '../api'

function Home() {
    const [movieList, setMovieList] = useState([])
    const [movieName, setmovieName] = useState([])

    const handleInput = (e) => {
        setmovieName(e.target.value)
    }

    useEffect(() => {
        const fetchMovieList = async () => {
            const result1 = await getMovieList();
            const result2 = await getKMDB('미나리');
            const movieList1 = result1.boxOfficeResult.dailyBoxOfficeList;
            const movieList2 = result2;
            console.log(movieList1);
            console.log(movieList2);
        }
        fetchMovieList();
    }, [])


    const getNaverMovie = async () => {
        try {
            const res = await axios.get('http://localhost:5000/naver/getNaverMovie', {
                params: {
                    query: movieName,
                },
            });
            if(res && res.status === 200) {
                console.log(res.data);
                setMovieList(res.data.items)
            }
        } catch (err) {
            console.log('error', err);
        }
    }
    
    return (
        <AppLayout>
            <input placeholder="영화를 검색하세요." onChange={handleInput} />
            <button onClick={getNaverMovie}></button>
            <div>
                {
                    movieList && 
                    movieList.map((item, index) => (
                        <div key={index}>
                            <div>{item.title}</div>
                            <img src={item.image}/>
                        </div>
                    ))
                }
            </div>
        </AppLayout>
    )
}

export default Home;
