export const initialState = {
    sendMovieDone: false,
    sendMovieError: null,
    movie: null,
};

export const SEND_MOVIE_REQUEST = 'SEND_MOVIE_REQUEST';


const reducer = (state=initialState, action) => {
    switch (action.type) {
        case SEND_MOVIE_REQUEST:
            console.log('reducer Review');
            return state = {
                ...state,
                sendMovieDone: false,
                movie: action.data,
            }

        default:
            return state;
    }
};

export default reducer;
