import React from 'react'
import Slider from "react-slick";

function MovieStllSlider({ movieActorInfo }) {
    const settings = {
        dots: false, 
        infinite: true,
        slidesToShow: 5,
        slidesToScroll: 5,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:"wrap-slider",
        initialSlide: 0,
        responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 650,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
        ],
    };

    const imageURL = 'https://image.tmdb.org/t/p/w100/'

    return (
        <Slider {...settings}>
            {
                movieActorInfo.map(item => (
                    <div>
                        <div>{item.name}</div>
                        <img src={`${imageURL}${item.profile_path}`} style={{ }} />
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieStllSlider
