import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { useRouter } from "next/router";
import { imageURL } from '../../config/config';
import { addReviewRequestAction } from '../../reducers/review';

const ModalWrap = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    backdrop-filter: blur(3px);
    z-index: 999999;
`

const Modal = styled.div`
    width: 600px;
    height: 600px;
    border-radius: 5px;
    padding: 20px;
    background-color: #2A2A28;
    box-sizing: border-box;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
`

const CloseBtn = styled.button`
    position: absolute;
    top: 20px;
    right: 20px;
`

const SubmitBtn = styled.button`
    position: absolute;
    bottom: 20px;
    right: 20px;
`
// 수정 페이지 - 작성 페이지에서 동일하게 사용되도록
function ReviewModal({ movie, closeModal }) {
    const dispatch = useDispatch();
    const router = useRouter();
    console.log(router.query);

    const [text, setText] = useState("");
    const { me } = useSelector((state) => state.user);

    const handleChangeText = (e) => {
        setText(e.currentTarget.value);
    };
    console.log(text);
    
    const handleSubmitReview = (e) => {
        e.preventDefault();
        const body = {
            writer: router.query.all ? router.query.all[0] : me._id,
            image: `${imageURL}/original${movie.poster_path ? movie.poster_path : movie.image}`,
            text: text,
            likes: 0,
            title: movie.title,
            movieId: router.query.all ? router.query.id : router.query.all[1],
        };
        dispatch(addReviewRequestAction(body));
        Router.push(`/myPage/${me._id}`);
    };

    return (
        <ModalWrap>
            <Modal>
                <div style={{ fontSize: '25px', marginBottom: '20px' }}>Review</div>
                <div style={{ display: 'flex' }}>
                    <div style={{ width: '50%' }}>
                        <div>
                            <img src={`${imageURL}/w300/${movie.poster_path ? movie.poster_path : movie.image}`} style={{ borderRadius: '5px', width: '260px' }} />
                        </div>
                        <div style={{ width: '260px' }}>
                            {movie.title}
                        </div>
                    </div>
                    <form onSubmit={handleSubmitReview} style={{ width: '50%' }}>
                        <textarea
                            placeholder="영화 리뷰를 적어주세요."
                            onChange={handleChangeText}
                            value={movie.text ? movie.text : ""}
                            style={{ width: '100%', height: '100%' }}
                        />
                        <SubmitBtn type="submit">제출</SubmitBtn>
                    </form>
                </div>
                <CloseBtn onClick={closeModal}>닫기</CloseBtn>
            </Modal>
        </ModalWrap>
    )
};

export default ReviewModal;
