import React from 'react'
import Slider from "react-slick";

function MovieSlider({ movieInfomation }) {
    const settings = {
        dots: true, 
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 3,
        speed: 500,
        arrows: true,
        autoplay: false,
        className:""
    };

    return (
        <Slider {...settings}>
            {
                movieInfomation.map(item => (
                    <div key={item.id} style={{ display: 'inline-block' }}>
                        <img src={item.poster} />
                        <div>{item.name}</div>
                        <button>예매</button>
                    </div>
                ))
            }
        </Slider>

    )
}

export default MovieSlider
