import axios from 'axios';
import { all, fork, call, put, takeLatest } from 'redux-saga/effects';
import {
    LOAD_MOVIE_FROM_TMDB_REQUEST,
    LOAD_MOVIE_FROM_TMDB_SUCCESS,
    LOAD_MOVIE_FROM_TMDB_FAILURE,
} from '../reducers/tmdb';


function addReviewAPI(data) {
    return axios.get(`https://api.themoviedb.org/3/search/movie?api_key=2b2e61361a6d20a07e728e56fc296333&query=${data.movieName}&language=ko&page=${data.pageNum}`)
}


function* addReview(action) {
    try {
        const result = yield call(addReviewAPI, action.data)
        console.log('saga LOAD_MOVIE_FROM_TMDB');  
        yield put({
            type: LOAD_MOVIE_FROM_TMDB_SUCCESS,
            data: result.data
        })
    } catch(err) {
        yield put({
            type: LOAD_MOVIE_FROM_TMDB_FAILURE,
            data: err.response.data
        })
    }
}


function* watchReview() {
    yield takeLatest(LOAD_MOVIE_FROM_TMDB_REQUEST, addReview);
}


export default function* reviewSaga() {
    yield all([
      fork(watchReview),
      
    ]);
}
