import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { END } from 'redux-saga';

import wrapper from '../store/configureStore';
import SubPageLayout from '../components/layout/SubPageLayout';
import MovieTalkCard from '../components/Card/MovieTalkCard';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import { LOAD_ALL_REVIEWS_REQUEST } from '../reducers/review';

function movieTalk() {
    const dispatch = useDispatch();

    const { allReviews, hasMoreReviews } = useSelector((state) => state.review);
    const { me } = useSelector((state) => state.user);
    const [id, setId] = useState(undefined);

    useEffect(() => {
        if(me && me._id) {
            setId(me._id);
        };
    }, []);

    useEffect(() => {
        function onScroll() {
            console.log(window.pageYOffset, document.documentElement.clientHeight, document.documentElement.scrollHeight - 300);
            if (window.pageYOffset + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
                if (hasMoreReviews) {
                    const lastId = allReviews[allReviews.length - 1]?._id;
                    // if(!lastId) return 0;
                    dispatch({
                        type: LOAD_ALL_REVIEWS_REQUEST,
                        lastId
                    });
                }
              }
        }
        window.addEventListener('scroll', onScroll);
        return () => {
          window.removeEventListener('scroll', onScroll);
        };
    }, []);

    return (
        <SubPageLayout>
            <div style={{ marginTop: '48px' }}>
            {
                allReviews && (
                    allReviews.map(review => (
                        <MovieTalkCard key={review._id} review={review} id={id} />
                    ))
                )
            }
            </div>
        </SubPageLayout>
    )
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
      axios.defaults.headers.Cookie = cookie;
    };
    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    context.store.dispatch({
        type: LOAD_ALL_REVIEWS_REQUEST,
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();
});

export default movieTalk;
