import React from 'react'
import styled from 'styled-components';

const ReviewCardWrap = styled.div`
    border: 1px solid gray;
    width: 45%;
    padding: 20px;
    border-radius: 3px;
    box-sizing: border-box;
    background: ${props => props.src || "black"}

    & > div + div {
        margin-top: 15px;
    }
`

function ReviewCard() {
    return (
        <ReviewCardWrap src={posterPath}>
            <div style={{ fontSize: '1.5rem' }}>제목</div>
            <div>★★★★★</div>
            <div>후기 내용 ...</div>
            <button style={{ marginTop: '15px' }}>더보기</button>
        </ReviewCardWrap>
    )
}

export default ReviewCard
