import Link from 'next/link';
import { useDispatch } from 'react-redux';

import { getInputValueAction } from '../reducers/movie'

function MovieInfoLink({ id, src, title, genre, rating, repRlsDate, runtime }) {
    const dispatch = useDispatch();
    
    const dispatchMovieInfo = () => {
        const body = {
          src: src,
          title: title,
          genre: genre,
          rating: rating,
          repRlsDate: repRlsDate,
          runtime: runtime,
        }
        dispatch(getInputValueAction(body))
    }

    return (
      <div>
        <Link href="/movieInfo/[id]" as={`/movieInfo/${id}`}>
          <a onClick={dispatchMovieInfo}>
            <img src={src} style={{ height: '310px', verticalAlign: 'top' }} />
          </a>
        </Link>
      </div>
    )
};

export default MovieInfoLink;
