import Link from 'next/link';
import React from 'react';
import fetch from 'node-fetch'
import { useRouter } from "next/router";
import AppLayout from '../../components/AppLayout';

import { getMovieSeqFromKMDB } from '../../api'

function MovieInfo ({ shows }) {
    const router = useRouter();
    const { id } = router.query;
    // console.log(movieList);

    return (
        <AppLayout>
            <Link href="/movieTalk">
                { id }
                리뷰쓰기
            </Link>
        </AppLayout>
    )
};

export async function getStaticPaths() {
	const res = await fetch(`http://api.koreafilm.or.kr/openapi-data2/wisenut/search_api/search_json2.jsp?collection=kmdb_new2&movieSeq=83546&ServiceKey=QXYS65CA4DKL68FJYGTF`)
    const list = await res.json();
    const result = list.Data[0].Result

    const paths = result.map((item) => ({
        params: { id: item.movieSeq },
    }))

	return {
		paths,
		fallback: false,
	};
}

export async function getStaticProps({ params }) {
	const res = await fetch(`http://api.koreafilm.or.kr/openapi-data2/wisenut/search_api/search_json2.jsp?collection=kmdb_new2&movieSeq=${params.id}&ServiceKey=QXYS65CA4DKL68FJYGTF`);
	const list = await res.json();

	return {
		props: { shows: list },
	};
}

export default MovieInfo;
